import React, { Component } from "react";
import axios from "axios";
import API from "../utils/API";

class Employees extends Component {
  //States
  state = {
    employees: {},
    search: "",
  };
  //Methods
  componentDidMount() {
    this.searchEmployees("Josh");
  }

  //API Search
  searchEmployees() {
    axios.get(`https://randomuser.me/api/?results=20&nat=Aus`).then((res) => {
      this.setState({ employees: res.data.results });
    });
  }

  //handling search input
  handleInputChange = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    this.setState({
      [name]: value,
    });
  };

  //Handle form submission, search the API for the value of "this.state.search"
  handleSubmitForm = (event) => {
    event.preventDefault();
    this.searchEmployees(this.state.search);
  };

  //Render
  render() {
    return (
      <div className="container">
        <table className="table table-hover">
          <thead className="bg-dark text-warning">
            <tr>
              <th>Image</th>
              <th>Name</th>
              <th>Phone</th>
              <th>Email</th>
              <th>DOB</th>
            </tr>
          </thead>
          <tbody>
            {this.state.employees.map(function () {
              return (
                <tr>
                  <td>
                    <img
                      alt="employee image"
                      src={this.employees.picture.thumbnail}
                    ></img>
                  </td>
                  <td>
                    {this.employees.name.first} {this.employees.name.last}
                  </td>

                  <td>{this.employees.email}</td>
                  <td>{this.employees.phone}</td>
                  <td>{this.Employees.dob.date}</td>
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    );
  }
}

export default Employees;
