import React, { useState, useEffect } from "react";
// import API from "../utils/API";
import axios from "axios";

function Employees() {
  const [emps, setEmployees] = useState([]);
  const [filterInput, setFilterInput] = useState("");

  useEffect(() => {
    // THIS WILL RUN ONLY AFTER YOUR 1ST RENDER
    axios("https://randomuser.me/api/?results=100&nat=us").then((result) => {
      console.log(result);
      // if (result) {
      //   setEmployees(result.data.results);
      // }
    });
  }, []);

  // useEffect(() => {         // THIS WILL RUN WHEN THERE'S A CHANGE IN 'quotes'
  //    if (emps) {
  //    setEmployees([])
  //    console.log("why")
  //     // setSomeOtherState();   // YOU CAN USE IT TO SET SOME OTHER STATE
  //    }
  // },[emps]);
  // {
 let nameFirst; 
  // Update the state when input changes
  const handleFilterChange = (e) => {
    const value = e.target.value || undefined;
    setFilterInput(value)
    console.log(value);
  };
 {
   if (filterInputChange === null) {
     {
       nameFirst = employees.name.first;
     }
   } else if (filterInputChange === value) {
     {
       nameFirst = employees.name.value;
     }
   }
 }
  return (
    <div>
      <div className="container">
        <div className="row"></div>

        <div className="input-group mb-3 col-md">
          <div className="input-group-prepend">
            <button
              className="btn btn-outline-dark"
              type="button"
              id="button-addon1"
              value={filterInput}
              onClick={handleFilterChange}
            >
              Search
            </button>
          </div>
          <input
            onChange={handleFilterChange}
            id="search"
            type="text"
            className="form-control"
            placeholder={"Enter search information"}
            aria-label="Example text with button addon"
            aria-describedby="button-addon1"
          ></input>
        </div>
      </div>
      <div className="container">
        <table className="table table-hover">
          <thead className="bg-dark text-warning">
            <tr>
              <th>Image</th>
              <th>Name</th>
              <th>Phone</th>
              <th>Email</th>
              <th>DOB</th>
            </tr>
          </thead>
          {emps ? (
            <tbody>
              {emps.map((employees) => {
                return (
                  <tr>
                    <td>
                      <img alt="img" src={employees.picture.thumbnail}></img>
                    </td>
                    <td>

                      {/* {employees.name.first} */}
                      {employees.name.last}
                    </td>
                    <td>{employees.email}</td>
                    <td>{employees.phone}</td>
                    <td>{employees.dob.date}</td>
                  </tr>
                );
              })}
            </tbody>
          ) : (
            <></>
          )}
        </table>
      </div>
    </div>
  );
}

export default Employees;
