import React, { Component } from "react";
import API from "../utils/API";

function Employees() {
  const [data, setData] = useState({ hits: [] });
  useEffect(async () => {
    const result = await axios("https://randomuser.me/api/?results=200&nat=us");
    setData(result.data);
  }, []);

  //Handle form submission, search the API for the value of "this.state.search"
  handleFormSubmit = (event) => {
    event.preventDefault();
    this.searchEmployees(this.state.search);
  };

  return (
    <div className="container">
      <table className="table table-hover">
        <thead className="bg-dark text-warning">
          <tr>
            <th>Image</th>
            <th>Name</th>
            <th>Phone</th>
            <th>Email</th>
            <th>DOB</th>
          </tr>
        </thead>
        <tbody>
          {this.state.employees.map((employees) => {
            return (
              <tr>
                <td>
                  <img alt="img" src={employees.picture.thumbnail}></img>
                </td>
                <td>
                  {employees.name.first} {employees.name.last}
                </td>
                <td>{employees.email}</td>
                <td>{employees.phone}</td>
                <td>{employees.dob.date}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default Employees;
