import React, { Component } from "react";
import API from "../utils/API";

class Employees extends Component {
  //States
  state = {
    employees: [],
    search: "",
  };
  //Methods
  componentDidMount() {
    this.searchEmployees("Josh");
  }

  //API Search
  searchEmployees = (query) => {
    API.search(query).then((res) => {
      this.setState({ employees: res.data.results });
      console.log(res.data.results);
    });
  };

  //handling search input
  handleInputChange = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    this.setState({
      [name]: value,
    });
    console.log(value);
  };

  sortNames = () => {
    const sortedEmployees = this.state.employees.sort((a, b) =>
      a.name > b.name ? 1 : -1
    );
    this.setState(sortedEmployees);
  };

  //Render
  render() {
    return (
      <div>
        <div className="container">
          <div className="row"></div>

          <div className="input-group mb-3 col-md">
            <div className="input-group-prepend">
              <button
                className="btn btn-outline-dark"
                type="button"
                id="button-addon1"
                onClick={this.handleInputChange}
              >
                Search
              </button>
            </div>
            <input
              onChange={this.handleInputChange}
              value={this.state.search}
              name="name"
              id="search"
              type="text"
              className="form-control"
              placeholder="Enter search information"
              aria-label="Example text with button addon"
              aria-describedby="button-addon1"
            ></input>
          </div>
        </div>
        <div className="container">
          <table className="table table-hover">
            <thead className="bg-dark text-warning">
              <tr>
                <th>Image</th>
                <th>Name</th>
                <th>Phone</th>
                <th>Email</th>
                <th>DOB</th>
              </tr>
            </thead>
            <tbody>
              {this.state.employees
                .filter(
                  (employees) =>
                    employees.name
                      .toLowerCase()
                      .includes(this.state.search.toLowerCase()) ||
                    employees.phone.includes(this.state.search)
                )
                .map((employees) => {
                  return (
                    <tr>
                      <td>
                        <img alt="img" src={employees.picture.thumbnail}></img>
                      </td>
                      <td>
                        {employees.name.first} {employees.name.last}
                      </td>
                      <td>{employees.email}</td>
                      <td>{employees.phone}</td>
                      <td>{employees.dob.date}</td>
                    </tr>
                  );
                })}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default Employees;
