import React, { Component } from "react";
import SearchForm from "./components/SearchForm/SearchForm";
import API from "./components/utils/API.js";

class App extends Component {
  //States
  state = {
    search: "",
    employees: [],
  };
  //Methods
  componentDidMount() {
    this.searchEmployees("Josh");
  }

  //API Search
  searchEmployees = (query) => {
    API.search(query).then((res) => {
      this.setState({ employees: res.data.results });
      console.log(res.data.results);
    });
  };

  refreshPage() {
    console.log("clicked");
    location.reload();
  }

  //handling search input
  handleInputChange = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    this.setState({
      [name]: value,
    });
    console.log(value);
  };

  handleFormSubmit = (e) => {
    e.preventDefault();
    console.log("Button Clicked", this.state.search, e);
    this.searchEmployee(this.state.search);
  };

  //Render
  render() {
    return (
      <div>
        <SearchForm />
        <div className="container">
          <table className="table table-hover">
            <thead className="bg-dark text-warning">
              <tr>
                <th>Image</th>
                <th>Name</th>
                <th>Phone</th>
                <th>Email</th>
                <th>DOB</th>
              </tr>
            </thead>
            <tbody>
              {this.state.employees.map((employees) => {
                return (
                  <tr>
                    <td>
                      <img alt="img" src={employees.picture.thumbnail}></img>
                    </td>
                    <td>
                      {employees.name.first} {employees.name.last}
                    </td>
                    <td>{employees.email}</td>
                    <td>{employees.phone}</td>
                    <td>{employees.dob.date}</td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      </div>
    );
  }
}
export default App;
