import React, { useState, useEffect } from "react";
import API from "../utils/API";
import axios from "axios";

function Employees() {
  const [emps, setEmployees] = useState({ hits: [] });

  // {
  //   hits: [];
  // }
  useEffect(async () => {
    const fetchData = async ()=>{
    const result = await axios("https://randomuser.me/api/?results=100&nat=us");}
    // console.log('result', result)
    setEmployees(result.data.results);
  }, []);

  // //Handle form submission, search the API for the value of "this.state.search"
  // const handleFormSubmit = (event) => {
  //   event.preventDefault();
  // };

  console.log("data", emps);
  return (
    <div className="container">
      <table className="table table-hover">
        <thead className="bg-dark text-warning">
          <tr>
            <th>Image</th>
            <th>Name</th>
            <th>Phone</th>
            <th>Email</th>
            <th>DOB</th>
          </tr>
        </thead>
        <tbody>
          {emps.hits.map(employees => {
            return (
              <tr>
                <td>
                  <img alt="img" src={employees.picture.thumbnail}></img>
                </td>
                <td>
                  {employees.name.first} {employees.name.last}
                </td>
                <td>{employees.email}</td>
                <td>{employees.phone}</td>
                <td>{employees.dob.date}</td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default Employees;
