import React, { useState, useEffect } from "react";
import API from "../utils/API";
import axios from "axios";

function Employees() {

  const [emps, setEmployees] = useState([]);
  const [filterInput, setFilterInput] = useState("");

  useEffect(() => {         // THIS WILL RUN ONLY AFTER YOUR 1ST RENDER
    axios("https://randomuser.me/api/?results=100&nat=us")
    .then((result) => {
      console.log(result);
      if(result)
      setEmployees(result.data.results); // HERE YOU SET quotes AND IT WILL TRIGGER A NEW RENDER
    });
    }, []);                 // BECAUSE YOU'VE SET IT WITH '[]'

  // useEffect(() => {         // THIS WILL RUN WHEN THERE'S A CHANGE IN 'quotes'
